mapper.select:
SELECT c.ID id, c.TITLE title, c.CATEGORY category, c.CONTENT content, c.IMAGES images, c.PUBTIME pubtime, u.NAME pubuser, c.STATUS status, u.TYPE FROM T_CASE c JOIN T_USER u ON c.PUBUSER = u.ID
c.CATEGORY = #{category, jdbcType=INTEGER}
AND c.STATUS = #{status, jdbcType=INTEGER}
AND c.PUBUSER = #{pubuser, jdbcType=INTEGER}
ORDER BY c.PUBTIME DESC LIMIT #{start, jdbcType=INTEGER}, #{limit, jdbcType=INTEGER}

SELECT 
	COUNT(c.ID)
FROM T_CASE c JOIN T_USER u ON c.PUBUSER = u.ID

	
		c.STATUS = #{status, jdbcType=INTEGER}
	
	
		AND c.PUBUSER = #{pubuser, jdbcType=INTEGER}
